import pytest
from height import height

TEST_CASES = [
    (0, 14, 0),
    (2, 0, 0),
    (2, 14, 105),
    (7, 20, 137979),
    (477, 10000, )
]  # respectivelly n, m and height(n, m)


@pytest.mark.parametrize('n, m, expected_height', TEST_CASES)
def test_height(n, m, expected_height):
    assert height(n, m) == expected_height
